import React,{useEffect, useState} from 'react'
import { addItemTocart } from './helper/cartHelper';
import ImageHelper from './helper/ImageHelper';
import {redirect} from 'react-router-dom'

const Card = ({
    product,
    addToCart=true,
    removeFromCart=false
}) => {
    const[redirect,setRedirect] = useState(false);
    const[count, setCount] = useState(count.product)
    const cardTitle= product ? product.name :" A Photo From Wefive "
    const cardDescription= product ? product.description :" Default Description "
    const cardPrice= product ? product.price :" Default "
    
    const addtoCart =()=>{
      addItemTocart(product, ()=> setRedirect(true))
    }

    const getARedirect = (redirect) =>{
      if(redirect){
        return(<Redirect to="/cart" />)
      }
    }

    const showAddToCart = (addToCart)=>{
        return(
            addToCart && (
            <button
                onClick={addtoCart}
                className="btn btn-block btn-outline-success mt-2 mb-2"
              >
                Add to Cart
              </button>
              )
        )
    }

    const showRemoveFromCart = (removeFromCart)=>{
        return(
            removeFromCart && (
            <button
                onClick={() => {}}
                className="btn btn-block btn-outline-danger mt-2 mb-2"
              >
                Remove from cart
              </button>
              )
        )
    }
    return (
      <div className="card text-white bg-dark border border-info text-center">
        <div className="card-header lead text-center">{cardTitle}</div>
        <div className="card-body">
          {getARedirect}
          <ImageHelper product={product}/>
          <p className="lead bg-success font-weight-normal text-wrap">
            {cardDescription}
          </p>
    <p className="btn btn-success rounded  btn-sm px-4">$ {cardPrice}</p>
          <div className="row">
            <div className="col-12">
                {showAddToCart(addToCart)}
            </div>
            <div className="col-12">
              {showRemoveFromCart(removeFromCart)}
            </div>
          </div>
        </div>
      </div>
    );
  };
export default Card
